import { useState } from "react"

const Example = () => {
    const [ countA, setCountA] = useState()
    return (
        <>
            <p>ボタンAを{countA}会押しました！</p>
            <button onClick={() => {
                setCountA(countA + 1);
            }}>Aボタン</button>
        </>
    )
};

export default Example;
